const Sequelize = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  return lojas.init(sequelize, DataTypes);
}

class lojas extends Sequelize.Model {
  static init(sequelize, DataTypes) {
  return super.init({
    idloja: {
      autoIncrement: true,
      type: DataTypes.INTEGER,
      allowNull: false,
      primaryKey: true
    },
    nomefantasia: {
      type: DataTypes.STRING(50),
      allowNull: true
    }
  }, {
    sequelize,
    tableName: 'lojas',
    schema: 'public',
    timestamps: false,
    indexes: [
      {
        name: "lojas_pkey",
        unique: true,
        fields: [
          { name: "idloja" },
        ]
      },
    ]
  });
  }
}
