const Sequelize = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  return produtos.init(sequelize, DataTypes);
}

class produtos extends Sequelize.Model {
  static init(sequelize, DataTypes) {
  return super.init({
    idproduto: {
      autoIncrement: true,
      type: DataTypes.INTEGER,
      allowNull: false,
      primaryKey: true
    },
    nome: {
      type: DataTypes.STRING(50),
      allowNull: false
    },
    codigo: {
      type: DataTypes.INTEGER,
      allowNull: false
    },
    idcategoria: {
      type: DataTypes.INTEGER,
      allowNull: true,
      references: {
        model: 'categorias',
        key: 'idcategoria'
      }
    }
  }, {
    sequelize,
    tableName: 'produtos',
    schema: 'public',
    timestamps: false,
    indexes: [
      {
        name: "produtos_pkey",
        unique: true,
        fields: [
          { name: "idproduto" },
        ]
      },
    ]
  });
  }
}
