const Sequelize = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  return relacaoprodutotamanho.init(sequelize, DataTypes);
}

class relacaoprodutotamanho extends Sequelize.Model {
  static init(sequelize, DataTypes) {
  return super.init({
    idproduto: {
      type: DataTypes.INTEGER,
      allowNull: false,
      primaryKey: true,
      references: {
        model: 'produtos',
        key: 'idproduto'
      }
    },
    idtamanho: {
      type: DataTypes.INTEGER,
      allowNull: false,
      primaryKey: true,
      references: {
        model: 'tamanhos',
        key: 'idtamanho'
      }
    },
    valor: {
      type: DataTypes.STRING(10),
      allowNull: false
    }
  }, {
    sequelize,
    tableName: 'relacaoprodutotamanho',
    schema: 'public',
    timestamps: false,
    indexes: [
      {
        name: "pk_ce",
        unique: true,
        fields: [
          { name: "idproduto" },
          { name: "idtamanho" },
        ]
      },
    ]
  });
  }
}
