const Sequelize = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  return taxasentrega.init(sequelize, DataTypes);
}

class taxasentrega extends Sequelize.Model {
  static init(sequelize, DataTypes) {
  return super.init({
    idtaxa: {
      autoIncrement: true,
      type: DataTypes.INTEGER,
      allowNull: false,
      primaryKey: true
    },
    descricao: {
      type: DataTypes.STRING(50),
      allowNull: false
    },
    valor: {
      type: DataTypes.STRING(10),
      allowNull: false
    }
  }, {
    sequelize,
    tableName: 'taxasentrega',
    schema: 'public',
    timestamps: false,
    indexes: [
      {
        name: "taxasentrega_pkey",
        unique: true,
        fields: [
          { name: "idtaxa" },
        ]
      },
    ]
  });
  }
}
